@import "tailwindcss";
@import "tw-animate-css";

@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --font-sans: 'Plus Jakarta Sans', system-ui, sans-serif;
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-success: var(--success);
  --color-success-foreground: var(--success-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

:root {
  --radius: 0.75rem;

  /* Background - warm off-white */
  --background: hsl(210 20% 98%);
  --foreground: hsl(210 25% 15%);

  /* Card - pure white */
  --card: hsl(0 0% 100%);
  --card-foreground: hsl(210 25% 15%);

  /* Popover */
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(210 25% 15%);

  /* Primary - Deep Teal */
  --primary: hsl(175 55% 35%);
  --primary-foreground: hsl(0 0% 100%);

  /* Secondary - subtle gray */
  --secondary: hsl(210 15% 94%);
  --secondary-foreground: hsl(210 25% 25%);

  /* Muted */
  --muted: hsl(210 15% 94%);
  --muted-foreground: hsl(210 10% 50%);

  /* Accent - Warm Amber */
  --accent: hsl(40 90% 55%);
  --accent-foreground: hsl(40 90% 15%);

  /* Destructive */
  --destructive: hsl(0 70% 55%);
  --destructive-foreground: hsl(0 0% 100%);

  /* Success */
  --success: hsl(150 60% 40%);
  --success-foreground: hsl(0 0% 100%);

  /* Border & Input */
  --border: hsl(210 15% 90%);
  --input: hsl(210 15% 90%);
  --ring: hsl(175 55% 35%);

  /* Charts */
  --chart-1: hsl(175 55% 35%);
  --chart-2: hsl(40 90% 55%);
  --chart-3: hsl(150 60% 40%);
  --chart-4: hsl(210 25% 50%);
  --chart-5: hsl(0 70% 55%);

  /* Sidebar */
  --sidebar: hsl(0 0% 100%);
  --sidebar-foreground: hsl(210 25% 15%);
  --sidebar-primary: hsl(175 55% 35%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(175 55% 95%);
  --sidebar-accent-foreground: hsl(175 55% 25%);
  --sidebar-border: hsl(210 15% 90%);
  --sidebar-ring: hsl(175 55% 35%);

  /* Shadows */
  --shadow-sm: 0 1px 3px hsl(210 20% 50% / 0.08);
  --shadow-md: 0 1px 3px hsl(210 20% 50% / 0.08), 0 4px 12px hsl(210 20% 50% / 0.04);
  --shadow-lg: 0 4px 12px hsl(210 20% 50% / 0.12), 0 8px 24px hsl(210 20% 50% / 0.06);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground font-sans antialiased;
  }
}

